--Test numeric in the where cluase

create class test_class(a numeric(38), b double, c varchar(10));

insert into test_class values(12345678901234567890, 12345678901234567890, '1');
insert into test_class values(12345678901234567890123456789012345678, 12345678901234567890, '2');
insert into test_class values(  99999999999999999999999999999999999999, 12345678901234567890, '3');

select * from test_class where a in ( select a from test_class) order by 1,2,3;
select * from test_class where c in ('1') order by 1;
select * from test_class where a = 12345678901234567890 order by 1;
select * from test_class where a in (12345678901234567890) order by 1;
select * from test_class where a in (to_number('12345678901234567890')) order by 1;
select * from test_class where a = 12345678901234567890123456789012345677 + 1 order by 1;
select * from test_class where a in (12345678901234567890123456789012345677 + 1);		
