--+ holdcas on;
-- test cases on system parameter holding the maximum size of a string
-- (CHAR, NCHAR, VARCHAR , VARNCHAR, BIT, VARBIT) when created with :
-- SPACE, REPEAT, GROUP_CONCAT, CONCAT, CONCAT_WS, STRCAT ,INSERT, + (PLUS )




-- + PLUS
set system parameters 'string_max_size_bytes=64';
select '123456789012345678901234567890' + '1234567890123456789012345678901234';
select '123456789012345678901234567890' + '12345678901234567890123456789012345';

select n'123456789012345678901234567890' + n'12';
select n'123456789012345678901234567890' + n'123';


select b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'111111111111111111111111111111111';

select b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111111111111111111111111111111111111111111111111111' +
			 b'11111111111111111111111111111111';
			 



create table t1 (s1 char(32),s2 varchar(32), s3 national char varying(32));
insert into t1 values ('1234567890','123456789012345678901234567890',n'123456789012345');

select s1 + s1 from t1;
select s1 + s1 + '123' from t1;

select s2 + s2+ '1234' from t1;
select s2 + s2+ '12345' from t1;


select s3 + s3+  n'12' from t1;
select s3 + s3+  n'123' from t1;

drop t1;




set system parameters 'string_max_size_bytes=10000000';

commit;
--+ holdcas off;
