drop table if exists t1;
create table t1(i int, j double, t date);

create serial order_no start with 10 increment by 2 maxvalue 200 comment'from 10 to 200 by 2';
insert into t1 values(order_no.NEXT_VALUE,11.2,'2014-9-13');
insert into t1 values(order_no.NEXT_VALUE,13.2,'2014-9-12');
insert into t1 values(order_no.NEXT_VALUE,14.2,'2014-9-11');
insert into t1 values(order_no.NEXT_VALUE,2221.2,'2014-2-13');
insert into t1 values(order_no.NEXT_VALUE,131.2,'2014-7-13');
insert into t1 values(order_no.CURRENT_VALUE,114.2,'2014-2-13');

select * from t1 order by i,j;
select name, comment from db_serial;

alter serial order_no comment'new comment for serial';
select name, comment from db_serial;

-- alter serial comment with 1024 bytes
alter serial order_no comment'';

-- check serial
select name, comment from db_serial where name = 'order_no';

-- error case: alter serial comment with 1025 bytes
alter serial order_no comment'';

drop serial order_no;

drop table if exists t1;
